from tkinter import filedialog as fdfrom tkinter.messagebox import *from tkinter import *def Encryption(key):    key = check(key)    if key == 'error':        return    path = str(fd.askopenfile()).split("'")[1]    fin = open(path, 'rb')    image = fin.read()    fin.close()    image = bytearray(image)    for index, values in enumerate(image):        image[index] = values ^ key    fin = open(path, 'wb')    fin.write(image)    fin.close()    with open('C:\\Users\\user\\AppData\\keys.txt', 'a') as w:        w.write(f'path {path} Key {key}\n')    showinfo('Done', f'File get Saved at {path} with key: {key}')def check(d):    try:        d = int(d.get())    except ValueError:        showerror('error', 'KEY SHOULD BE INT')        return 'error'    if d > 255 or d < 0:        showerror('error', 'KEY SHOULD BE IN RANGE OF 0~255')        return 'error'    return droot = Tk()root.geometry('400x200')root.title('Mini project')rootFrame = Frame(root).pack()L=Label(rootFrame,text="KEY")L.pack()E = Entry(rootFrame)E.pack()BE = Button(rootFrame, text='Encryption', command=lambda: Encryption(E))BE.pack()root.mainloop()